import React from 'react';
import './App.css';
import { css } from "aphrodite/no-important";
import styles from "./AppStyles"
// import { css } from "aphrodite/no-important";
// import styles from "./AppStyles";
class App extends React.Component {
  render() {
    return (
      <form class="block__login">
        <img src="./Images/LOGO.svg" alt="LOGO" class="logo" />
        <h1>Войти в аккаунт</h1>
        <div class="form-group input__login">
          <i class="alert__top hidden">Неправильный логин, телефон или e-mail</i>
          <input minlength="1" maxlength="20" type="login" name="login" class="login" placeholder="Логин" autocomplete="on"></input>
          <i class="alert__bottom hidden">Ошибка</i>
        </div>
        <div class="form-group input__password">
          <i class="alert__top hidden">Неправильный пароль</i>
          <input maxlength="20" type="password" name="password" class="password" placeholder="Пароль" autocomplete="off"></input>
          <i href="#" class="password-control" aria-hidden="true"></i>
          <i class="alert__bottom hidden">Ошибка</i>
        </div>
        <button class="submit">Войти</button>
        <p><a href="index.html" lass="password__forgot">Забыли пароль?</a><a href="index.html" class="password__ask">Запросить новый пароль</a></p>
      </form >
    );
  }
}
export default App;
